import React, { useState } from 'react'
import ReactDOM from 'react-dom';

const App = (props) => {
  const [counter, setCounter] = useState(0)

  const handleClick = () => {
    console.log('clicked')
  }

  setTimeout(
    () => setCounter(counter + 1),
    1000
  )
  return (
    <div>
      <div>{counter}</div>
      <button onClick={handleClick}>
        plus
      </button>
    </div>

  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
